function pretty_fig (fig, font_size_multiplier)
    if nargin == 1
        font_size_multiplier = 1;
    end
    set(0,'defaulttextinterpreter','latex');
    
    hAllAxes  = findobj(fig,'type','axes');
    hLegend   = findall(fig,'tag','legend');
    hTitle    = findobj(fig,'tag','title');
    hXLabel   = findall(fig,'tag','xlabel');
    hYLabel   = findall(fig,'tag','ylabel');
    hText     = findall(hAllAxes,'Type','text');
    hColorbar = findobj(fig,'type','colorbar');

    set(hLegend, 'LineWidth', 0.1, 'Color', 'white');
    
    set([hLegend, hTitle, hXLabel, hYLabel, hText.'], ...
        'Interpreter', 'latex');
    
    set(hColorbar.', 'FontName' , 'AvantGarde', ...
                     'TickLabelInterpreter', 'latex'); 

    FONT_SIZE = 0.04 * font_size_multiplier;
    for h_text = [hXLabel, hYLabel, hText.']
        font_size = FONT_SIZE / h_text.Parent.Position(4);
        set(h_text, 'FontName' , 'AvantGarde', ... 
                    'FontUnits', 'normalized', ...
                    'FontSize', font_size);
    end

    set(hAllAxes, ...
        'Box'         , 'off'     , ...
        'TickDir'     , 'out'     , ...
        'TickLength'  , [.02 .02] , ...
        'XMinorTick'  , 'on'      , ...
        'YMinorTick'  , 'on'      , ...
        'YMinorGrid'       , 'off'      , ...
        'YGrid'       , 'off'      , ...
        'LineWidth'   , 1.5         , ...
        'FontName'   , 'AvantGarde', ...
        'FontUnits', 'normalized'    ,...
        'TickLabelInterpreter', 'latex');

    for ax = hAllAxes'
        font_size = FONT_SIZE / ax.Position(4);
        set(ax, 'FontSize', font_size)
    end 
end